["def parse_kv_pairs(s: str) -> dict[str, str]:
    out = {}
    for seg in s.split(','):
        seg = seg.strip()
        if not seg or '=' not in seg:
            continue
        k, v = seg.split('=', 1)
        k, v = k.strip(), v.strip()
        if not k:
            continue
        out[k] = v
    return out
